import React, {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css'
const Weather=()=>{
    const [city,setCity]=useState('');
    const [weatherData,setWeatherData]=useState(null);

    const fetchData= async()=>{
        try{
            const response=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=6bf9a983128db508b716928fe1935e3b`);
            setWeatherData(response.data);
            console.log(response.data);
        }
        catch(error){console.log(error);}
    };
    useEffect(()=>{
        fetchData();
    },[]);

const handleInputChange=(e)=>{
    setCity(e.target.value)
};
const handleSubmit=(e)=>{
    e.preventDefault();
    fetchData(); 
};
  return (
    <>
    <div>
        <form  onSubmit={handleSubmit}>
            <input type="text" placeholder="Enter Your City name" value={city}
            onChange={handleInputChange}/>
            <button type="submit">Submit</button>
        </form>
        {weatherData ?
        (<>
        <h2>{weatherData.name}</h2>
        <img 
                            className=""
                            src={`https://openweathermap.org/img/wn/${weatherData.dat.weatherData[0].icon}@2x.png`} 
                            alt={weatherData.data.weatherData[0].description} 
                        /> 
        <p>temperature: {weatherData.main.temp} °C</p>
        <p>feels like: {weatherData.main.feels_like} °C</p>
        </>):(
            <p>Loading Weather data ...</p>
        )}
    </div>
    </>
  )
        }

export default Weather
