import React, {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css'
import weather from './img/weather.jpg'
const Weather=()=>{
    const [city,setCity]=useState('');
    const [weatherData,setWeatherData]=useState(null);

    const fetchData= async()=>{
        try{
            const response=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=6bf9a983128db508b716928fe1935e3b`);
            setWeatherData(response.data);
            console.log(response.data);
        }
        catch(error){console.log(error);}
    };
    useEffect(()=>{
        fetchData();
    },[]);

const handleInputChange=(e)=>{
    setCity(e.target.value)
};
const handleSubmit=(e)=>{
    e.preventDefault();
    fetchData(); 
};
  return (
    <>
    <div className='id'>
        <div className='container-fluid  sec'>
        <form className='clas'  onSubmit={handleSubmit}>
            <input className='px-5 pt-' type="text" placeholder="Enter Your City name" value={city}
            onChange={handleInputChange}/>
            <button type="submit" className='btn btn-success mx-3'>Submit</button>
        </form>
        {weatherData ? 
        (<>
        <h2>{weatherData.name}</h2>
        <p>description : {weatherData.weather[0].description}%</p>
        <p>temperature: {weatherData.main.temp} °C</p>
        <p>feels like: {weatherData.main.feels_like} °C</p>
        <p>Humidity : {weatherData.main.humidity}%</p>
          <p>Pressure : {weatherData.main.pressure}</p>
          <p>Wind Speed : {weatherData.wind.speed}m/s</p> 
                        
        </>):(
            <p>Loading Weather data ...</p>
        )}
</div>
    </div>
    </>
  )
        }

export default Weather
